import React from 'react'
import { Select } from 'antd';
import TimeUtils from '../utils/timeUtil';

const { Option } = Select;

const CATEGORY = [
    {
        id: "firstpage",
        label: "首页",
        active: 1
    },
    {
        id: "frontend",
        label: "前端",
        active: 0
    },
    {
        id: "backend",
        label: "后端",
        active: 0
    },
    {
        id: "android",
        Label: "安卓",
        active: 0
    },
    {
        id: "ios",
        Label: "苹果",
        active: 0
    }
];

const handleChange = () => {}

const CategorySelector = () => {
    return (
        <Select defaultValue="frontend" style={{ width: 120 }} onChange={handleChange}>
            {
                CATEGORY.map(cate => {
                    return (
                        <Option value={cate.id} key={cate.id}>{cate.label}</Option>
                    )
                })
            }
        </Select>
    )
}



const OrderSelector = props => {
    const handleOrderChange = e => {
    };
    return (
        <>
            <div className="order-selector" onMouseOver={handleOrderChange}>
                <div className="hottest active">热门</div>
                <div className="latest">最新</div>
            </div>
            <style jsx>
                {
                    `
                        .order-selector {
                            margin-right: .8rem;
                            display:flex;
                            align-items: center;
                        }
                        .hottest, .latest {
                            display: inline-block;
                            margin: 0 .1rem;
                            width: 3.6rem;
                            height: 3.5rem;
                            font-size: 1.25rem;
                            text-align: center;
                            line-height: 3.5rem;
                            color: #646c7b;
                            cursor: pointer;
                            opacity: .3;
                        }
                        .active {
                            opacity: .8;
                        }
                    `
                }
            </style>
        </>
    );
}

const GoldArtical = props => {
    const { title, url, date, collectionCount, user } = props;
    const { iso } = date;
    const { username } = user;
    const badgeTmpl = count => (`至少已有${count}喜欢`);
    return (
        <>
            <li className="goldItem">
                <a className="itemContent" href={url} target="_blank">
                    <div className="badge" title={badgeTmpl(collectionCount)}></div>
                    <div className="entry-info">
                        <div className="title">{title}</div>
                        <div className="metalist">
                            <div className="meta-item">
                                <span className="meta-text" title={new Date(iso).toLocaleString()}>{TimeUtils.formatCustomTime(iso)}</span>
                            </div>
                            <div className="meta-item">
                                <span className="meta-text">{username}</span>
                            </div>
                        </div>
                    </div>
                </a>
            </li>
        </>
    )
}

const LeftColumn = (props) => (
    <div className="gold-source">
        <div className="source-navbar">
            <div className="source-selector">
                <img className="icon source-icon" src="data:image/svg+xml;base64," />
                <div className="title" style={{color: '#044aab'}}>掘金</div>
            </div>
            <div className="category-box">
                <CategorySelector />
            </div>
            <OrderSelector />
        </div>
        <div>
            <ul>
                {
                    props.articles.map(artical => {
                        return <GoldArtical {...artical} key={artical.id}/>
                    })
                }
            </ul>
        </div>
        <style jsx>
            {
                `
                    .gold-source {
                        flex: 0 0 auto;
                        width: 33.97rem;
                    }
                    .source-navbar {
                        display: flex;
                        align-items: center;
                        margin: 0 0 1.2rem;
                        min-height: 3.5rem;
                        background-color: #fff;
                        border-radius: 2px;
                        z-index: 250;
                        margin-right: .8rem!important;
                    }

                    .source-selector {
                        position: relative;
                        height: 100%;
                        font-size: 1.35rem;
                        font-family: Verdana,Geneva,Microsoft YaHei,Microsoft JhengHei,Helvetica Neue,sans-serif;
                        color: #646c7b;
                        display: flex;
                        align-items: center;
                        margin-right: .8rem!important;
                    }

                    .icon {
                        margin: 0 1rem 0 .4rem;
                    }

                    .source-icon {
                        width: 2.833rem;
                        height: 2.833rem;
                        border-radius: 2px;
                    }

                    .category-box {
                        flex-grow: 1;
                    }

                `
            }
        </style>
    </div>
)

export default LeftColumn;
