import React from 'react'
import { Select } from 'antd';

const { Option } = Select;

const CATEGORY = [
    {
        id: "firstpage",
        label: "首页",
        active: 1
    },
    {
        id: "frontend",
        label: "前端",
        active: 0
    },
    {
        id: "backend",
        label: "后端",
        active: 0
    },
    {
        id: "android",
        Label: "安卓",
        active: 0
    },
    {
        id: "ios",
        Label: "苹果",
        active: 0
    }
];

const handleChange = () => {}

const CategorySelector = () => {
    return (
        <Select defaultValue="frontend" style={{ width: 120 }} onChange={handleChange}>
            {
                CATEGORY.map(cate => {
                    return (
                        <Option value={cate.id} key={cate.id}>{cate.label}</Option>
                    )
                })
            }
        </Select>
    )
}

const LeftColumn = (props) => (
    <>
        <div className="source-navbar">
            <div className="source-selector">
                <img className="icon source-icon" src="data:image/svg+xml;base64," />
                <div className="title" style={{color: '#044aab'}}>掘金</div>
            </div>
            <div className="category-box">
                <CategorySelector />
            </div>
        </div>
        <div>
            <ul></ul>
        </div>
        <style jsx>
            {
                `
                    .source-navbar {
                        display: flex;
                        align-items: center;
                        margin: 0 0 1.2rem;
                        min-height: 3.5rem;
                        background-color: #fff;
                        border-radius: 2px;
                        z-index: 250;
                        margin-right: .8rem!important;
                    }

                    .source-selector {

                    }

                    .category-box {

                    }
                `
            }
        </style>
    </>
)

export default LeftColumn;
