import React from 'react'
import { Select } from 'antd';

const { Option } = Select;

const CATEGORY = [
    {
        id: "firstpage",
        label: "首页",
        active: 1
    },
    {
        id: "frontend",
        label: "前端",
        active: 0
    },
    {
        id: "backend",
        label: "后端",
        active: 0
    },
    {
        id: "android",
        Label: "安卓",
        active: 0
    },
    {
        id: "ios",
        Label: "苹果",
        active: 0
    }
];

const handleChange = () => {}

const CategorySelector = () => {
    return (
        <Select defaultValue="frontend" style={{ width: 120 }} onChange={handleChange}>
            {
                CATEGORY.map(cate => {
                    return (
                        <Option value={cate.id} key={cate.id}>{cate.label}</Option>
                    )
                })
            }
        </Select>
    )
}

const LeftColumn = (props) => (
    <div className="gold-source">
        <div className="source-navbar">
            <div className="source-selector">
                <img className="icon source-icon" src="data:image/svg+xml;base64," />
                <div className="title" style={{color: '#044aab'}}>掘金</div>
            </div>
            <div className="category-box">
                <CategorySelector />
            </div>
            <div className="order-selector">

            </div>
        </div>
        <div>
            <ul></ul>
        </div>
        <style jsx>
            {
                `
                    .gold-source {
                        flex: 0 0 auto;
                        width: 33.97rem;
                    }
                    .source-navbar {
                        display: flex;
                        align-items: center;
                        margin: 0 0 1.2rem;
                        min-height: 3.5rem;
                        background-color: #fff;
                        border-radius: 2px;
                        z-index: 250;
                        margin-right: .8rem!important;
                    }

                    .source-selector {
                        position: relative;
                        height: 100%;
                        font-size: 1.35rem;
                        font-family: Verdana,Geneva,Microsoft YaHei,Microsoft JhengHei,Helvetica Neue,sans-serif;
                        color: #646c7b;
                        display: flex;
                        align-items: center;
                        margin-right: .8rem!important;
                    }

                    .icon {
                        margin: 0 1rem 0 .4rem;
                    }

                    .source-icon {
                        width: 2.833rem;
                        height: 2.833rem;
                        border-radius: 2px;
                    }

                    .category-box {
                        flex-grow: 1;
                    }
                `
            }
        </style>
    </div>
)

export default LeftColumn;
