import Header from './Header'

const layoutStyle = {
    // margin: 20,
    // padding: 20,
    // border: '1px solid #DDD'

}

const Layout = props => (
    <>
        <div className="layoutStyle">
            <Header />
            {props.children}
        </div>
        <style jsx global>
            {
                `
                    .layoutStyle {
                        display: flex;
                        flex-direction: column;
                        position: absolute;
                        width: 100%;
                        height: 100%;
                        background-color: #eceff1;
                        overflow: hidden;
                    }
                    * {
                        box-sizing: border-box;
                    }
                `
            }
        </style>
    </>
)

export default Layout;
