    {
        id: "ios",
        Label: "苹果",
        active: 0
    }
];

const CategorySelector = () => {
    return (
        <Select defaultValue="frontend" style={{ width: 120 }} onChange={handleChange}>
            {
                CATEGORY.map(cate => {
                    return (
                        <Option value={cate.id}>{cate.label}</Option>
                    )
                })
            }
        </Select>
    )
}

